INSERT INTO `wp_postmeta` VALUES("28", "15", "developments-content", "<p>«СЗГЦ» и «Берлогу» вместе с другими подразделениями ОК «Сибшахтострой» мастера граффити увековечили «в красках на бетоне», и теперь наш город стал еще лучше, интереснее, красивее! По словам завсегдатаев Кузнецкого шоссе, автолюбители, проезжая мимо, даже снижают скорость — чтобы разглядеть и полюбоваться.</p>");
