INSERT INTO `wp_postmeta` VALUES("1207", "347", "services-preview", "<p>Сибирский завод горячего цинкования осуществляет оцинковку металлоизделий для различных сфер и отраслей: коммунальное хозяйство, горнодобывающая и химическая промышленность, отрасль связи, железнодорожная и нефтегазовая отрасли.</p>");
